mapper.select:
SELECT * FROM SHELF S WHERE USERNAME = #{userName} AND SHNAME LIKE CONCAT ('%', #{shName}, '%') AND SHMEMO LIKE CONCAT ('%', #{shMemo}, '%')
AND CREDATE BETWEEN #{creDateA} AND #{creDateB}
ORDER BY
SHNAME CREDATE
DESC ASC
LIMIT #{startRow}, #{perPage}

mapper.update:
UPDATE SHELF
	SET SHNAME = #{shName} AND SHMEMO = #{shMemo}
WHERE SHNUM = #{shNum}
