mapper.select:
SELECT COUNT(ISBN) FROM BOOK WHERE TITLE LIKE CONCAT('%', #{searchTitle}, '%') AND WRITER LIKE CONCAT('%', #{searchWriter}, '%') AND PUBLISHER LIKE CONCAT('%', #{searchPublisher}, '%')
AND ISBN LIKE CONCAT('%', #{searchISBN}, '%')
AND BOOKDATE BETWEEN #{searchDate1} AND #{searchDate2}
AND CATEGORY = #{searchCategory}

SELECT * FROM BOOK
ORDER BY REGDATE DESC
limit 0, #{perPage}
